create database IF not exists karnataka_pension_db;

use karnataka_pension_db;
CREATE TABLE IF NOT EXISTS karnataka_pensioners (
  id BIGINT NOT NULL AUTO_INCREMENT,
  full_name VARCHAR(255) NOT NULL,
  aadhaar_number VARCHAR(12) NOT NULL,
  pension_scheme VARCHAR(255) NOT NULL,
  state VARCHAR(255) NOT NULL,
  active BOOLEAN NOT NULL,
  status VARCHAR(20) NOT NULL,
  start_date DATE NULL,
  years_receiving INT NOT NULL,
  months_receiving INT NOT NULL,
  pension_amount INT NOT NULL,
  total_pension_received INT NOT NULL,
  bank_name VARCHAR(255) NOT NULL,
  account_number VARCHAR(20) NOT NULL,
  last_verification_date DATE NULL,
  mobile_number VARCHAR(15) NOT NULL,
  PRIMARY KEY (id)
);


select * from karnataka_pensioners;

delete from karnataka_pensioners where id >100;